    private void EKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_EKeyReleased
       E.setText(E.getText().replaceAll("[^0-9 | ^.]" , ""));        // TODO add your handling code here:
    }//GEN-LAST:event_EKeyReleased

    private void mKeyReleased(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_mKeyReleased
        m.setText(m.getText().replaceAll("[^0-9 | ^.]" , ""));        // TODO add your handling code here:
    }//GEN-LAST:event_mKeyReleased

    private void send3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_send3ActionPerformed
        if(E.getText().isEmpty()){
            double _m, res;
            String result;            
            _m = Double.parseDouble(m.getText());
            res = _m * Math.pow(299792458,2);
            result = Double.toString(res);
            resposta.setText(result);
        }
        if(m.getText().isEmpty()){
            double _E, res;
            String result;            
            _E = Double.parseDouble(E.getText());
            res = _E / Math.pow(299792458,2);
            result = Double.toString(res);
            resposta.setText(result);
        }
    }//GEN-LAST:event_send3ActionPerformed

